import { EntidadInterface } from "../interfaces/entidadInterface";
import { EstadisticasEntrenamiento } from "../interfaces/estadisticasEntrenamiento";

let contador_id = 0;
const EMPTY_ESTADISTICAS_ENTRENAMIENTO: EstadisticasEntrenamiento = { semana: { km: 0, desnivel: 0 }, mes: { km: 0, desnivel: 0 }, anio: { km: 0, desnivel: 0 } };

/**
 * Clase que representa a un grupo
 */
export class Grupo implements EntidadInterface {
  id: number;
  nombre: string;
  creador: number;
  participantes: number[] = [];
  estadisticasEntrenamiento: EstadisticasEntrenamiento = EMPTY_ESTADISTICAS_ENTRENAMIENTO;
  clasificacion: { id: number; km: number; desnivel: number }[] = [];
  rutasFavoritas: number[] = [];
  historicoRutas: { ruta: number; fecha: string; }[] = [];

  constructor(nombre: string, creador: number) {
    if (nombre === '') {
      throw new Error('Nombre de grupo vacío');
    }
    contador_id++;
    this.id = contador_id;
    this.nombre = nombre;
    this.creador = creador;
  }

  getID(): number {
    return this.id;
  }

  getNombre(): string {
    return this.nombre;
  }

  getCreador(): number {
    return this.creador;
  }

  getParticipantes(): number[] {
    return this.participantes;
  }

  getEstadisticasEntrenamiento(): {
    semana: { km: number; desnivel: number };
    mes: { km: number; desnivel: number };
    anio: { km: number; desnivel: number };
  } {
    return this.estadisticasEntrenamiento
  }

  getClasificacion(): { id: number; km: number; desnivel: number }[] {
    return this.clasificacion;
  }

  getRutasFavoritas(): number[] {
    return this.rutasFavoritas;
  }

  getHistoricoRutas(): { ruta: number; fecha: string;}[] {
    return this.historicoRutas;
  }

  setID = (id: number): void => {
    this.id = id;
    if ( id > contador_id ) {
      contador_id = id;
    }
  }

  setNombre(nombre: string): void {
    this.nombre = nombre;
  }

  setCreador(creador: number): void {
    this.creador = creador;
  }

  setParticipantes(participantes: number[]): void {
    this.participantes = participantes;
  }

  setEstadisticasEntrenamiento( estadisticasEntrenamiento: {
    semana: { km: number; desnivel: number };
    mes: { km: number; desnivel: number };
    anio: { km: number; desnivel: number };
  }): void {
    this.estadisticasEntrenamiento = estadisticasEntrenamiento;
  }

  setClasificacion(clasificacion: { id: number; km: number; desnivel: number }[]): void {
    this.clasificacion = clasificacion;
  }

  setRutasFavoritas(rutasFavoritas: number[]): void {
    this.rutasFavoritas = rutasFavoritas;
  }

  setHistoricoRutas(historicoRutas: {ruta: number; fecha: string;
  }[]): void {
    this.historicoRutas = historicoRutas;
  }

  addRutaFavorita(idRuta: number): void {
    this.rutasFavoritas.push(idRuta);
  }

  addRutaRealizada(ruta: { ruta: number; fecha: string; }): void {
    this.historicoRutas.push(ruta);
  }

  eraseRutaFavorita(idRuta: number): void {
    this.rutasFavoritas = this.rutasFavoritas.filter((id) => id !== idRuta);
  }

  addParticipante(id: number): void {
    this.participantes.push(id);
  }

  eraseParticipante(idParticipante: number): void {
    this.participantes = this.participantes.filter((id) => id !== idParticipante);
  }

}

import { EntidadInterface } from "../interfaces/entidadInterface";
import { Actividad } from "../enums/actividadEnum";
import { Dificultad } from "../enums/dificultadEnum";
import { Coordenadas } from "../interfaces/coordenadasInterface";

let contador_id = 0;

export class Ruta implements EntidadInterface {
  id: number;
  nombre: string;
  coordenadasInicio: Coordenadas;
  coordenadasFin: Coordenadas;
  longitud: number;
  desnivel: number;
  usuariosVisitantes: number[] = [];
  tipoActividad: Actividad;
  dificultad: Dificultad;
  calificacion: number = 0;

  constructor(
    nombre: string, 
    coordenadasInicio: Coordenadas, 
    coordenadasFin: Coordenadas, 
    longitud: number, 
    desnivel: number, 
    tipoActividad: Actividad,
    dificultad: Dificultad) 
    {
      contador_id++;
      this.id = contador_id;
      this.nombre = nombre;
      this.coordenadasInicio = coordenadasInicio;
      this.coordenadasFin = coordenadasFin;
      this.longitud = longitud;
      this.desnivel = desnivel;
      this.tipoActividad = tipoActividad;
      this.dificultad = dificultad;

      // Ponemos una calificación aleatoria entre 0 y 5
      this.calificacion = Math.floor(Math.random() * 6);
    }    

  // Métodos
  getID(): number {
    return this.id;
  }
  getNombre(): string {
    return this.nombre;
  }
  getCoordenadasInicio(): Coordenadas {
    return this.coordenadasInicio;
  }
  getCoordenadasFin(): Coordenadas {
    return this.coordenadasFin;
  }
  getLongitud(): number {
    return this.longitud;
  }
  getDesnivel(): number {
    return this.desnivel;
  } 
  getUsuariosVisitantes(): number[] {
    return this.usuariosVisitantes;
  }
  getTipoActividad(): Actividad {
    return this.tipoActividad;
  }
  getDificultad(): Dificultad {
    return this.dificultad;
  }
  getCalificacion(): number {
    return this.calificacion;
  }

  setID = (id: number): void => {
    this.id = id;
    if ( id > contador_id ) {
      contador_id = id;
    }
  }

  setNombre(nombre: string): void {
    this.nombre = nombre;
  }
  setCoordenadasInicio(coordenadasInicio: Coordenadas): void {
    this.coordenadasInicio = coordenadasInicio;
  }
  setCoordenadasFin(coordenadasFin: Coordenadas): void {
    this.coordenadasFin = coordenadasFin;
  }
  setLongitud(longitud: number): void {
    this.longitud = longitud;
  }
  setDesnivel(desnivel: number): void {
    this.desnivel = desnivel;
  }
  setUsuariosVisitantes(usuariosVisitantes: number[]): void {
    this.usuariosVisitantes = usuariosVisitantes;
  }
  setTipoActividad(tipoActividad: Actividad): void {
    this.tipoActividad = tipoActividad;
  }
  setDificultad(dificultad: Dificultad): void {
    this.dificultad = dificultad;
  }
  setCalificacion(calificacion: number): void {
    this.calificacion = calificacion;
  }
  addUsuarioVisitante(idUsuario: number): void {
    this.usuariosVisitantes.push(idUsuario);
  }
  removeUsuarioVisitante(idUsuario: number): void {
    // Lo borramos de usuariosVisitantes
    this.usuariosVisitantes = this.usuariosVisitantes.filter( id => id !== idUsuario );
  }

}

/**
 * Enumerado de actividades
 */
export enum Actividad {
  ciclismo = 'ciclismo',
  running = 'running',
}

/**
 * Enumerado de dificultados
 */
export enum Dificultad {
  facil = 'facil',
  media = 'media',
  dificil = 'dificil',
}

import { EntidadInterface } from "../interfaces/entidadInterface";
import { Actividad } from "../enums/actividadEnum";
import { EstadisticasEntrenamiento } from "../interfaces/estadisticasEntrenamiento";

let contador_id = 0;
/**
 * Clase que representa a un usuario
 */
export class Usuario implements EntidadInterface {
  id: number;
  nombre: string;
  contraseña: string;
  actividades: Actividad; // deberia ser un array de actividades
  amigosApp: number[] = [];
  amigosFrecuentes: number[] = [];
  estadisticas: EstadisticasEntrenamiento = { semana: { km: 0, desnivel: 0 }, mes: { km: 0, desnivel: 0 }, anio: { km: 0, desnivel: 0 } };
  rutasFavoritas: number[] = [];
  retosActivos: number[] = [];
  historicoRutas: { ruta: number; fecha: string; }[] = [];

  constructor(nombre: string, contraseña: string, actividades: Actividad) {
    /// Comprobamos que el nombre no esté vacío y que la actividad sea válida
    if (nombre === '') {
      throw new Error('Nombre de usuario vacío');
    }
    if (this.isValidPassword(contraseña) === false) {
      throw new Error('Contraseña no válida');
    }
    contador_id++;
    this.id = contador_id;
    this.nombre = nombre;
    this.contraseña = contraseña;
    this.actividades = actividades;
  }

  *[Symbol.iterator]() {
    yield this.nombre;
    yield* this.actividades;
  }

  getID(): number {
    return this.id;
  }

  getNombre(): string {
    return this.nombre;
  }

  getContraseña(): string {
    return this.contraseña;
  }

  getActividades(): Actividad {
    return this.actividades;
  }

  getAmigosApp(): number[] {
    return this.amigosApp;
  }

  getAmigosFrecuentes(): number[] {
    return this.amigosFrecuentes;
  }

  getEstadisticas(): {
    semana: { km: number; desnivel: number };
    mes: { km: number; desnivel: number };
    anio: { km: number; desnivel: number };
  } {
    return this.estadisticas;
  }

  getRutasFavoritas(): number[] {
    return this.rutasFavoritas;
  }

  getRetosActivos(): number[] {
    return this.retosActivos;
  }

  getHistoricoRutas(): { ruta: number; fecha: string; }[] {
    return this.historicoRutas;
  }

  setID = (id: number): void => {
    this.id = id;
    if ( id > contador_id ) {
      contador_id = id;
    }
  }

  // Métodos de modificación
  setNombre(nombre: string): void {
    this.nombre = nombre;
  }

  setContraseña(contraseña: string): void {
    this.contraseña = contraseña;
  }

  setActividades(actividades: Actividad): void {
    this.actividades = actividades;
  }

  setAmigosApp(amigosApp: number[]): void {
    this.amigosApp = amigosApp;
  }

  setAmigosFrecuentes(amigosFrecuentes: number[]): void {
    this.amigosFrecuentes = amigosFrecuentes;
  }

  setEstadisticas(estadisticas: {
    semana: { km: number; desnivel: number };
    mes: { km: number; desnivel: number };
    anio: { km: number; desnivel: number };
  }): void {
    this.estadisticas = estadisticas;
  }

  setRutasFavoritas(rutasFavoritas: number[]): void {
    this.rutasFavoritas = rutasFavoritas;
  }

  setRetosActivos(retosActivos: number[]): void {
    this.retosActivos = retosActivos;
  }

  setHistoricoRutas(historicoRutas: { ruta: number; fecha: string; }[]): void {
    this.historicoRutas = historicoRutas;
  }

  addAmigoApp(idAmigo: number): void {
    // Compruebamos si el id del amigo ya está en la lista
    if (this.amigosApp.includes(idAmigo)) {
      throw new Error('El amigo ya está en la lista de amigos');
    }
    this.amigosApp.push(idAmigo);
  }

  eraseAmigoApp(idAmigo: number): void {
    this.amigosApp = this.amigosApp.filter((id) => id !== idAmigo);
  }

  addRutaFavorita(idRuta: number): void {
    this.rutasFavoritas.push(idRuta);
  }

  addRutaRealizada(ruta: { ruta: number; fecha: string; }): void {
    this.historicoRutas.push(ruta);
  }

  eraseRutaFavorita(idRuta: number): void {
    this.rutasFavoritas = this.rutasFavoritas.filter((id) => id !== idRuta);
  }

  addRetosActivos(idReto: number): void {
    this.retosActivos.push(idReto);
  }

  eraseRetosActivos(idReto: number): void {
    this.retosActivos = this.retosActivos.filter((id) => id !== idReto);
  }

  /**
   * Validador de la contraseña
   * @param contraseña Contraseña a validar
   */
  isValidPassword(password: string): boolean {
    if(password.length < 4 || password.length > 30) {
      return false;
    }
    if(password[0] === '_' || password[password.length - 1] === '_') {
    return false;
    }

    const hasUppercase = /[A-Z]/.test(password);
    const hasLowercase = /[a-z]/.test(password);
    const hasNumber = /[0-9]/.test(password);
    const hasSpecial = /[$,-,_,*]/.test(password);

    if (!hasUppercase || !hasLowercase || !hasNumber || !hasSpecial) {
      return false;
    }
    return true;
  }
}
